class FileSystemOutput {
  //debug 开启async语法
  constructor(system, debug) {
    var inputfilesystem = new FileSystemInput(5000);
    if (debug) {
      this.system = system || Object.assign(new ConverOutPut(true), makeCallbackToPromise(inputfilesystem, ["wake"]), { purge: inputfilesystem.purge });
    } else {
      this.system = system || Object.assign(new ConverOutPut(), inputfilesystem, { purge: inputfilesystem.purge });
    }

    //添加同步方法

    Object.getOwnPropertyNames(Object.getPrototypeOf(this)).forEach(methodName => {

      if (methodName == "constructor") {
        return;
      }

      this[methodName + "Sync"] = makeSyncFunc(this[methodName],null,[pt],["pt"]);

      if (!debug) {
        this[methodName] = makeCallbackFunc(this[methodName]);
      }

    });
  }

  // appendFile，如果文件不存在，会自动创建新文件  
  // 如果用writeFile，那么会删除旧文件，直接写新文件  
  //写数据
  async write(file, data, append, encode) {
    var params = {};
    if (typeof append == "boolean") {
      params.append = append
    }
    if (typeof append == "string") {
      params.encode = append
    } else if (typeof encode == "string") {
      params.encode = encode
    }


    //创建文件
    await this.create(file, true);

    if (!await this.system.isFile(file)) {
      console.error("this path is not file!");
      return;
    }

    if (params.append) {
      //flag:"a"表示追加
      await this.system.appendFile(file, data, { encoding: params.encode || 'utf8', mode: 438 /*=0666*/ , flag: 'a' });
    } else {

      await this.system.writeFile(file, data, { encoding: params.encode || 'utf8', mode: 438 /*=0666*/ , flag: 'w' });
    }

    this.system.purge("all", file);

    return true;

  }

  //刪除
  async remove(src) {
    if (await this.system.exists(src)) {
      if (!await this.system.isDir(src)) {
        await this.system.unlink(src); // 删除文件
        this.system.purge("all", src);
      } else {
        let fileList = await this.system.findAll(src);
        let len = fileList && fileList.length
        //尽量不要用forEach
        for (let i = 0; i < len; i++) {
          await this.remove(fileList[i]); //递归删除
        }
        await this.system.rmdir(src); //删除文件夹
        this.system.purge("all", src);
      }
    }
  }

  //拷貝,overwrite覆盖
  async copy(src, dir, overwrite) {

    let params = {};
    if (typeof overwrite == "boolean") {
      params.overwrite = overwrite;
    } else {
      params.overwrite = true;
    }

    if (await this.system.exists(src)) {

      if (!await this.system.isDir(src)) {

        if (!params.overwrite) {
          if (!await this.system.exists(dir)) {
            await this.create(dir, true);
            await this.system.copyFile(src, dir);
            this.system.purge("all", dir);
          } else {
            return false;
          }
        } else {
          //创建文件
          await this.create(dir, true);

          // let srcModify = await this.system.getModify(src);
          // let dirModify = await this.system.getModify(dir);

          //如果文件存在而且文件内容不相同时覆盖
          // if (srcModify != dirModify) {
            await this.system.copyFile(src, dir);
            this.system.purge("all", dir);
          // }
        }
      } else {
        let fileList = await this.system.findFile(src, true);
        let len = fileList || fileList.length || 0;
        let relativePath = pt.relative(src, dir);
        while (len--) {
          await this.copy(fileList[len], `${dir}/${relativePath}`, params.overwrite);
        }
      }

    } else {
      console.warn(`${src} not exists`);
      return false;
    }

    return true;;
  }

  //剪切
  async cut(src, dir, overwrite) {

    if (await this.system.exists(src)) {

      var flag = await this.copy(src, dir, overwrite);

      if (flag) {
        return await this.remove(src);
      }
    } else {
      console.warn(`${src} not exists`);
    }
    return false;

  }

  //重命名
  async rename(src, dir) {

    if (await this.system.exists(src)) {

      let path = pt.dirname(src);

      let dirName = pt.basename(dir);

      let dirpath = `${path}/${dirName}`;

      if(!await this.system.exists(dirpath) ){

        await this.system.rename(src,dirpath );

        this.system.purge("all", src);
  
        this.system.purge("all", dirpath);
      }else{
        console.log(`rename fail dir:${dirpath} is exists`);
      }

    } else {
      console.warn(`rename fail source:${src} not exists`);
    }


  }
  //必须指定是创建文件还是创建目录
  async create(createDirPath, isFile) {

    if (!createDirPath) {
      return;
    }

    if (await this.system.exists(createDirPath)) {
      return;
    }

    //最外层肯定要存在,才可以创建文件
    let parentDirPath = pt.dirname(createDirPath);

    if (parentDirPath && !await this.system.exists(parentDirPath)) {
      await this.create(parentDirPath, false);
    }
    //必须指定
    if (isFile) {
      await this.system.writeFile(createDirPath, "");
    } else {
      await this.system.mkdir(createDirPath);
    }

    this.system.purge("stat", createDirPath);


  }

}

module.exports =FileSystemOutput;

