
//解析参数
function convertParams(path, fileType, deep, filterFn, callback) {
  var obj = {path:path};

  if (typeof fileType == "string") {
    obj.fileType = fileType;
  } 

  if (typeof fileType == "boolean") {
    obj.deep = fileType;
  } else if(typeof deep=="boolean"){
    obj.deep = deep;
  }


  [fileType, deep, filterFn, callback].forEach(val=>{
    if(typeof val=="function"){
      if(!obj.filter ){
        obj.filter = val;
      }else{
        obj.callback = val;
      }
    }
  });

//只有一个fun的时候是callback
if(obj.filter&&!obj.callback){
  obj.callback = obj.filter;
  delete obj.filter;
}

  return obj
}
module.exports = convertParams;

