"use strict";

module.exports = function(path){
    //统一路径格式
	if(!path || /^(http:|https:|\/\/|\\\\)/.test(path)){
		return path;
	}
	return path.replace(/(\/|\\)+/g,"/").replace(/(\/|\\)$/,"");
    
}